import React, { Component, Fragment } from 'react'
import { withRouter, Redirect } from 'react-router-dom'

import { getTransaction } from '../DB/DB'

class ViewTransactions extends Component {
  render() {
    const { userAddress } = this.props.info

    let transactions = undefined

    if(userAddress === null) {
      console.log('고객 정보가 없습니다.')
      return <Redirect to='/' />
    } else {
      transactions = getTransaction(userAddress)
      console.log(transactions)
    }

    return (
      <Fragment>
        <table>
          <thead>
            <tr>
              <th>날짜</th>
              <th>내용</th>
              <th>출금</th>
              <th>입금</th>
              <th>잔액</th>
            </tr>
          </thead>
          <tbody>
          {transactions.map((transaction, index) => {
            const { purpose, date, balance, amount } = transaction
            const splitedDate = date.split(' ')

            return (
              <Fragment>
                <tr key={index}>
                  <td>{splitedDate}</td>
                  <td>{purpose}</td>
                  <td>
                    {purpose === '입금' && 0}
                    {purpose === '출금' && amount}
                  </td>
                  <td>
                    {purpose === '입금' && amount}
                    {purpose === '출금' && 0}
                  </td>
                  <td>{balance}</td>
                </tr>
              </Fragment>
            )
          })}
          </tbody>
        </table>
      </Fragment>
    )
  }
}

export default withRouter(ViewTransactions)
