import React, { Component, Fragment } from 'react'
import { withRouter, Redirect } from 'react-router-dom'

import { getTransaction } from '../DB/DB'

class ViewTransactions extends Component {
  render() {
    const { userAddress } = this.props.info

    let transactions = undefined

    if(userAddress === null) {
      console.log('고객 정보가 없습니다.')
      return <Redirect to='/' />
    } else {
      transactions = getTransaction(userAddress)
      console.log(transactions)
    }
    
    return (
      <Fragment>
        <table>
          <thead>
            <tr>
              <th>날짜</th>
              <th>내용</th>
              <th>출금</th>
              <th>입금</th>
              <th>잔액</th>
            </tr>
          </thead>
          <tbody>
          {() => transactions.map(transaction => {
            return (
              <tr>
                <td>{transaction.date}</td>
                <td>{transaction.purpose}</td>
                <td>
                  {transaction.purpose === '입금' && 0}
                  {transaction.purpose === '출금' && transaction.amount}
                </td>
                <td>
                  {transaction.purpose === '입금' && transaction}
                  {transaction.purpose === '출금' && 0}
                </td>
                <td>{transaction.balance}</td>
              </tr>
            )
          })}
          </tbody>
          {/* {() => transactions.map(transaction => {
            return (
              <tr>
                <td>{transaction.date}</td>
                <td>{transaction.purpose}</td>
                {transaction.purpose === '입금' && (
                  <Fragment>
                    <td>0</td>
                    <td>{transaction.amount}</td>
                  </Fragment>
                )}
                {transaction.purpose === '출금' && (
                  <Fragment>
                    <td>{transaction.amount}</td>
                    <td>0</td>
                  </Fragment>
                )}
                <td>{transaction.balance}</td>
              </tr>
            )
          })} */}
        </table>
      </Fragment>
    )
  }
}

export default withRouter(ViewTransactions)
